// Debug the actual HTML being generated by the verification email function
import { createClient } from '@supabase/supabase-js';
import dotenv from 'dotenv';

// Load environment variables
dotenv.config();

const supabaseUrl = process.env.VITE_SUPABASE_URL;
const supabaseServiceRoleKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

if (!supabaseUrl || !supabaseServiceRoleKey) {
  console.error('‚ùå Missing environment variables');
  console.error('Need VITE_SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY');
  process.exit(1);
}

// Use service role key to get more detailed logs
const supabase = createClient(supabaseUrl, supabaseServiceRoleKey);

async function debugEmailHTML() {
  console.log('üîç Debugging verification email HTML generation...');
  
  // Test payload
  const payload = {
    email: 'denniserciaaguila@gmail.com',
    name: 'HTML Debug Test',
    verificationToken: 'html_debug_123',
    userType: 'cat-parent'
  };
  
  console.log('üì¶ Sending payload:', JSON.stringify(payload, null, 2));
  
  try {
    const { data, error } = await supabase.functions.invoke('send-verification-email', {
      body: payload
    });
    
    if (error) {
      console.error('‚ùå Edge Function error:', error);
      console.error('‚ùå Full error object:', JSON.stringify(error, null, 2));
    } else {
      console.log('‚úÖ Function response:', data);
      
      // Check the Supabase function logs for detailed output
      console.log('üîç Check the Supabase function logs at:');
      console.log('üìä https://supabase.com/dashboard/project/fahqkxrakcizftopskki/functions/send-verification-email/logs');
      console.log('');
      console.log('üí° The logs should show the siteUrl and logoSrc values used in the template');
    }
  } catch (err) {
    console.error('‚ùå Exception:', err);
  }
}

debugEmailHTML();